mapper.select:
SELECT fa.*,
at.asset_type_id AS "assetType.assetTypeId",
at.type_name AS "assetType.typeName",
at.description AS "assetType.description"
FROM fixed_asset fa
LEFT JOIN asset_type at ON fa.asset_type_id = at.asset_type_id


        AND fa.asset_id = #{assetId}


        AND fa.asset_type_id = #{assetTypeId}


        AND fa.name = #{name}


        AND fa.purchase_date = #{purchaseDate}


        AND fa.price = #{price}


        AND fa.img_dir = #{imgDir}


        AND fa.status = #{status}
